import { Component, OnInit } from '@angular/core';
import {DomSanitizer} from '@angular/platform-browser';

@Component({
  selector: 'app-base64-encode',
  templateUrl: './base64-encode.page.html',
  styleUrls: ['./base64-encode.page.scss'],
})
export class Base64EncodePage {

  base64Image = 'data:image/jpg;base64, ';
  ciao = {
    "1": "data:image/jpg;base64, ",
    "2": "data:image/jpg;base64, ",
    "3": "data:image/jpg;base64, "
  };
  //Constructor Required
  constructor(private sanitizer: DomSanitizer){}

  //Call this method in the image source, it will sanitize it.
  transform(ciao) {
    return this.sanitizer.bypassSecurityTrustResourceUrl(ciao);
  }



}
